import React, { Fragment, useState } from 'react'

import child from './child/Counter'

function Parent () {
  const [count, setCount] = useState(0)
  return (
    <Fragment>
      <p>{count}</p>
      <button onClick={() => {
        setCount(count+1)
      }}>累加</button>
    </Fragment>
  )
}

export default Parent
