import React, { Fragment, useState, createContext, useContext, useEffect } from 'react'

import './Counter'

const countContext = createContext()

function App() {
  const [count, setCount] = useState(0)
  useEffect(Counter(), [])
  return (
    <Fragment>
      <p>{count}</p>
      <button onClick={() => {
        setCount(count+1)
      }}>累加</button>
      {
        /* 
          这段代码就相当于把count变量允许跨层级实现传递和使用了（也就是实现了上下文），
          当父组件的count变量发生变化时，子组件也会发生变化 
        */
      }
      <countContext.Provider value={count}></countContext.Provider>
    </Fragment>
  )
}

export default App
