import React, { Fragment, useState, createContext, useContext } from 'react'
const CountContext = createContext()
// 子组件
function Counter () {
  let count = useContext(CountContext)
  return (<h2>{count}</h2>
  )
}
// 父组件
function App() {
  const [count, setCount] = useState(0)
  return (
    <Fragment>
      <p>{count}</p>
      <button onClick={() => {
        setCount(count+1)
      }}>累加</button>
      {
        /* 
          这段代码就相当于把count变量允许跨层级实现传递和使用了（也就是实现了上下文），
          当父组件的count变量发生变化时，子组件也会发生变化 
        */
      }
      <CountContext.Provider value={count}><Counter></Counter></CountContext.Provider>
    </Fragment>
  )
}

export default App
