import React, { Component, Fragment } from 'react'

class App extends Component {
  constructor (props) {
    super(props)
    this.state = {
      inputValue: '',
      list: []
    }
  }
  render () {
    return (
      <Fragment>
        <input value={this.state.inputValue} placeholder="输入内容" onChange={(e) => {
          this.setState({
            inputValue: e.target.value
          })
        }}/>
        <button onClick={() => {
          this.setState({
            list: [...this.state.list, this.state.input]
          })
        }}>增加</button>
      </Fragment>
    )
  }
}

export default App
