import React, { Component, Fragment } from 'react';

class Item extends Component {
  constructor (props) {
    super(props)
    this.state = {
      inputValue: '',
      list: []
    }
  }
  render () {
    return (
      <Fragment>
        <ul>
          {
            this.state.list.map((item, index) => {
              return (
                <li key={index} onClick={(index) => {
                  let list = this.state.list
                  list.splice(index, 1)
                  this.setState({
                    list
                  })
                }}>{item}</li>
              )
            })
          }
        </ul>
      </Fragment>
    )
  }
}

export default Item;
