import React, { Component, Fragment } from 'react'

class App extends Component {
  constructor (props) {
    super(props)
    this.state = {
      inputValue: '',
      list: []
    }
  }
  deleteItem () {

  }
  render () {
    return (
      <Fragment>
        <input value={this.state.inputValue} placeholder="输入内容" onChange={(e) => {
          this.setState({
            inputValue: e.target.value
          })
        }}/>
        <button onClick={() => {
          this.setState({
            list: [...this.state.list, this.state.inputValue],
            inputValue: ''
          })
        }}>增加</button>
        <ul>
          {
            this.state.list.map((item, index) => {
              return (
                <li key={index} onClick={() => {
                  this.deleteItem()
                }}>{item}</li>
              )
            })
          }
        </ul>
      </Fragment>
    )
  }
}

export default App
