import React, { Component, Fragment } from 'react';


class App extends Component {
  constructor (props) {
    super(props)
    this.state = {
      inputValue: '',
      list: []
    }
  }
  render () {
    return (
      <Fragment>
        <input value={this.state.inputValue} placeholder="输入内容" onChange={(e) => {
          this.setState({
            inputValue: e.target.value
          })
        }}/>
        <button onClick={() => {
          this.setState({
            list: [...this.state.list, this.state.inputValue],
            inputValue: ''
          })
        }}>添加爱好</button>
        <ul>
          {
            this.state.list.map((item, index) => {
              return (
                <li key={index} onClick={(index) => {
                  let list = this.state.list
                  list.splice(index, 1)
                  this.setState({
                    list
                  })
                }}>{item}</li>
              )
            })
          }
        </ul>
      </Fragment>
    )
  }
}

export default App;
