import React, {
  Component,
  Fragment
} from 'react';

class Item extends Component {
  constructor(props) {
    super(props)
    this.state = {
      inputValue: '',
      list: []
    }
  }
  render() {
    return ( <
      Fragment >
      <
      li key = {
        index
      }
      onClick = {
        (index) => {
          let list = this.state.list
          list.splice(index, 1)
          this.setState({
            list
          })
        }
      } > {
        item
      } < /li> <
      /Fragment>
    )
  }
}

export default Item;
